using Advanced_Combat_Tracker;
using System;
using System.Drawing;
using System.Drawing.Drawing2D;
using System.IO;
using System.Reflection;
using System.Windows.Forms;

[assembly: AssemblyTitle("ACT_3sTickBar")]
[assembly: AssemblyVersion("1.0.0.0")]
namespace ACT_3sTickBar
{
    public static class Const
    {
        //=================================================================
        // 3sTickBarを表示するジョブ名
        public static string[] ENABLE_JOB = { "BLM", };
        // 3sTickBarを表示する座標(x,y)
        public static Point LOCATION = new Point(340, 280);
        //=================================================================
        public enum JobEnum : byte
        {
            Adv,
            Gla,
            Pgl,
            Mrd,
            Lnc,
            Arc,
            Cnj,
            Thm,
            Crp,
            Bsm,
            Arm,
            Gsm,
            Ltw,
            Wvr,
            Alc,
            Cul,
            Min,
            Btn,
            Fsh,
            Pld,
            Mnk,
            War,
            Drg,
            Brd,
            Whm,
            Blm,
            Acn,
            Smn,
            Sch,
            Rog,
            Nin,
            Mch,
            Drk,
            Ast,
            Sam,
            Rdm,
            Blu,
            Gnb,
            Dnc,
        }
    }

    public class ACT_3sTickBar : IActPluginV1
    {
        private string PlayerID;
        private string PlayerJob;
        private string TickLog;

        private Label statusLabel;
        private Label labelX;
        private Label labelY;
        private TextBox textBoxX;
        private TextBox textBoxY;

        private TickBar tickBar;

        public void InitPlugin(TabPage pluginScreenSpace, Label pluginStatusText)
        {
            statusLabel = pluginStatusText;
            statusLabel.Text = "Plugin started";

            labelX = new Label();
            labelX.Location = new Point(10, 14);
            labelX.AutoSize = true;
            labelX.Text = "X:";
            textBoxX = new TextBox();
            textBoxX.Location = new Point(30, 10);
            textBoxX.AutoSize = false;
            textBoxX.Size = new Size(50, 20);
            textBoxX.Text = Const.LOCATION.X.ToString();
            textBoxX.TextChanged += textBox_TextChanged;
            pluginScreenSpace.Controls.Add(labelX);
            pluginScreenSpace.Controls.Add(textBoxX);

            labelY = new Label();
            labelY.Location = new Point(100, 14);
            labelY.AutoSize = true;
            labelY.Text = "Y:";
            textBoxY = new TextBox();
            textBoxY.Location = new Point(120, 10);
            textBoxY.AutoSize = false;
            textBoxY.Size = new Size(50, 20);
            textBoxY.Text = Const.LOCATION.Y.ToString();
            textBoxY.TextChanged += textBox_TextChanged;
            pluginScreenSpace.Controls.Add(labelY);
            pluginScreenSpace.Controls.Add(textBoxY);


            tickBar = new TickBar();
            tickBar.Show();
            tickBar.Hide();

            ActGlobals.oFormActMain.BeforeLogLineRead += oFormActMain_BeforeLogLineRead;

            statusLabel.Text = "Plugin Inited.";
        }

        private void textBox_TextChanged(object sender, EventArgs e)
        {
            try
            {
                Const.LOCATION.X = int.Parse(textBoxX.Text);
                Const.LOCATION.Y = int.Parse(textBoxY.Text);
                tickBar.Location = Const.LOCATION;
            }
            catch (Exception)
            {
                textBoxX.Text = Const.LOCATION.X.ToString();
                textBoxY.Text = Const.LOCATION.Y.ToString();
            }
        }

        public void DeInitPlugin()
        {
            ActGlobals.oFormActMain.BeforeLogLineRead -= oFormActMain_BeforeLogLineRead;

            if (tickBar != null)
            {
                tickBar.Dispose();
                tickBar = null;
            }

            statusLabel.Text = "Plugin exited";
        }

        private void oFormActMain_BeforeLogLineRead(bool isImport, LogLineEventArgs logInfo)
        {
            if (isImport) return;

            string log = logInfo.logLine;
            int length = log.Length;

            if (length < 40) return;

            // 01:Changed Zone
            if (log.IndexOf("01:", 15, 3, StringComparison.Ordinal) == 15)
            {
                tickBar.stopTick();
                return;
            }

            // 20:Changed primary player
            if (log.IndexOf("02:", 15, 3, StringComparison.Ordinal) == 15)
            {
                PlayerID = logInfo.originalLogLine.Substring(37, 8).ToUpper();
                TickLog = "27:" + PlayerID;
                return;
            }

            // 0C:Player Stats: JOBID:
            if (log.IndexOf("0C:", 15, 3, StringComparison.Ordinal) == 15)
            {
                byte playerJobID = byte.Parse(log.Substring(32, log.IndexOf(":", 32) - 32));
                if (Enum.IsDefined(typeof(Const.JobEnum), playerJobID))
                {
                    PlayerJob = Enum.GetName(typeof(Const.JobEnum), playerJobID);
                    foreach (string job in Const.ENABLE_JOB)
                    {
                        if (PlayerJob.Equals(job, StringComparison.OrdinalIgnoreCase))
                        {
                            if (!tickBar.Visible) tickBar.Show();
                            return;
                        }
                    }
                }
                TickLog = null;
                tickBar.stopTick();
                tickBar.Hide();
                return;
            }

            // 27:NetworkUpdateHP
            if (TickLog != null &&
                log.IndexOf(TickLog, 15, 11, StringComparison.Ordinal) == 15)
            {
                //tickBar.startTick(logInfo.detectedTime);
                tickBar.startTick(DateTime.Now);
                return;
            }

            // 21:80000000:40000010: (wipe)
            if (log.IndexOf("21:", 15, 3, StringComparison.Ordinal) == 15 &&
                log.IndexOf("40000010", 27, 8, StringComparison.Ordinal) == 27)
            {
                ActGlobals.oFormActMain.ActCommands("end");
                tickBar.stopTick();
                return;
            }
        }
    }

    #region TickBar
    public class TickBar : Form
    {
        private static readonly byte[] IMG_BACK = Convert.FromBase64String("");
        private static readonly byte[] IMG_FORE = Convert.FromBase64String("");
        private static readonly Bitmap imgBack = new Bitmap(new MemoryStream(IMG_BACK, 0, IMG_BACK.Length));
        private static readonly Bitmap imgFore = new Bitmap(new MemoryStream(IMG_FORE, 0, IMG_FORE.Length));

        private static readonly StringFormat stringFormat = new StringFormat();
        private static readonly Pen pen = new Pen(Color.FromArgb(121, 85, 22), 2F);
        private static readonly Brush foreBrush = new SolidBrush(Color.FromArgb(222, 215, 190));

        private static readonly Font TimeFont = new Font("Meiryo", 14);

        private DateTime startTickTime = DateTime.MinValue;
        private DateTime nextTickTime = DateTime.MinValue;
        private Timer timer1;

        public TickBar()
        {
            this.TopMost = true;
            this.ControlBox = false;
            this.ShowInTaskbar = false;
            this.FormBorderStyle = FormBorderStyle.None;   // hidden border
            this.BackColor = Color.FromArgb(85, 90, 112);
            this.TransparencyKey = BackColor;
            this.SetStyle(ControlStyles.OptimizedDoubleBuffer | ControlStyles.AllPaintingInWmPaint | ControlStyles.SupportsTransparentBackColor, true);


            this.AutoScaleDimensions = new SizeF(6F, 12F);
            this.AutoScaleMode = AutoScaleMode.Font;
            this.Location = Const.LOCATION;
            this.StartPosition = FormStartPosition.Manual;
            this.Padding = new Padding(0);
            this.Margin = new Padding(0);
            this.Name = "TickBar";
            this.Text = "TickBar";

            this.SuspendLayout();

            this.ClientSize = new Size(200, 20);

            this.BackgroundImageLayout = ImageLayout.None;
            this.BackgroundImage = imgBack;

            this.ResumeLayout(false);

            this.timer1 = new Timer();
            this.timer1.Interval = 10;
            this.timer1.Enabled = false;
            this.timer1.Tick += new EventHandler(this.timer1_Tick);

            stringFormat.LineAlignment = StringAlignment.Center;
            pen.LineJoin = LineJoin.Round;
        }

        public void startTick(DateTime time)
        {
            this.startTickTime = time;
            this.nextTickTime = this.startTickTime.AddSeconds(3d);
            if (!this.timer1.Enabled)
            {
                this.Invoke(new MethodInvoker(delegate
                {
                    this.timer1.Enabled = true;
                }));
            }
            if (!this.Visible) this.Show();
        }

        public void stopTick()
        {
            this.startTickTime = DateTime.MinValue;
            this.nextTickTime = DateTime.MinValue;
            this.Invoke(new MethodInvoker(delegate
            {
                this.timer1.Enabled = false;
            }));
            this.Invalidate(false);
        }

        private void timer1_Tick(object sender, EventArgs e)
        {
            if (DateTime.Now > this.nextTickTime)
            {
                this.startTickTime = this.nextTickTime;
                this.nextTickTime = this.startTickTime.AddSeconds(3d);
            }
            this.Invalidate(false);
        }

        protected override CreateParams CreateParams
        {
            get
            {
                CreateParams cp = base.CreateParams;
                // Set the form click-through
                cp.ExStyle |= 0x80000 /* WS_EX_LAYERED */ | 0x20 /* WS_EX_TRANSPARENT */;
                return cp;
            }
        }

        protected override void Dispose(bool disposing)
        {
            if (this.timer1 != null)
            {
                this.timer1.Enabled = false;
                this.timer1.Dispose();
                this.timer1 = null;
            }
            imgBack.Dispose();
            imgFore.Dispose();
            foreBrush.Dispose();
            pen.Dispose();

            base.Dispose(disposing);
        }

        protected override void OnPaint(PaintEventArgs e)
        {
            DateTime now = DateTime.Now;
            if (this.startTickTime == DateTime.MinValue)
            {
                now = this.startTickTime;
            }

            TimeSpan ts = now - this.startTickTime;
            double percentage = ts.TotalMilliseconds / 3000;
            int pix = Convert.ToInt32(154 * percentage);

            using (GraphicsPath path = new GraphicsPath())
            {
                Rectangle srcRect = new Rectangle(0, 0, pix, imgFore.Height);
                Rectangle desRect = new Rectangle(0, 0, srcRect.Width, srcRect.Height);

                Graphics g = e.Graphics;
                g.DrawImage(imgFore, desRect, srcRect, GraphicsUnit.Pixel);

                path.AddString(string.Format("{0:0.0}", ts.TotalMilliseconds / 1000), TimeFont.FontFamily, (int)TimeFont.Style, TimeFont.Size, new Point(160, 7), stringFormat);
                g.TextRenderingHint = System.Drawing.Text.TextRenderingHint.ClearTypeGridFit;
                g.SmoothingMode = SmoothingMode.HighQuality;
                g.DrawPath(pen, path);
                g.FillPath(foreBrush, path);
            }
        }
    }
    #endregion
}
